context.data = context.data || {};

switch (msg.topic) 
{
    case "time":
        context.data.timestamp = msg.payload;
        msg = null;
        break;
    case "infoArduino":
        context.data.info = msg.payload;
        msg = null;
        break;
    default:
        msg = null;
    	break;
}

if(context.data.timestamp != null && context.data.info != null) 
{
	res = {};
    res.payload = JSON.stringify(context.data);
    res.topic = "InfoAlert";
    context.data = null;
	return res;
}

var msgg = "";
var ret = {};
var input = [];

temp = msg.payload;
input = temp.split(":");
// A:idAlarma:idDispositivo:torre:apto
if(input[0] == "A") {
    switch (input[1]) 
    {
        case "1":
            msgg = "La puerta lleva abierta un largo periodo de tiempo.";
            break;
        case "2":
            msgg = "Intento de apertura sospechoso. Alguien ha ingresado la clave más de 3 veces de forma errónea";
            break;
        case "3":
            msgg = "Se detectó la presencia de una persona cerca de la cerradura, en un horario no permitido.";
            break;
        case "4":
            msgg = "Ingreso sospechoso. Alguien entró al inmueble en un horario no permitido.";
            break;
        case "5":
            msgg = "Nivel de batería crítico. Recargue la cerradura.";
            break;
        default:
            input[1] = 0;
            msgg = "El error no puede ser identificado";
        	break;
    }

    ret.topic = "infoArduino";
    ret.payload = {
        "alertaId":input[1],
        "mensajeAlerta":msgg,
        "idDispositivo":input[2],
        "unidadResidencial":input[3],
        "torre":input[4],
        "apto":input[5]
    };
    return ret;
}

